import * as React from 'react';
import './ModalContent.css';
import {
    Button, Dialog, DialogActions, DialogContent, DialogContentText,
    DialogTitle, FormControl, Input, InputLabel, MenuItem,
    Select, Slider
} from '@mui/material';


function ModalContent() {
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <React.Fragment>
            <Button variant="outlined" onClick={handleClickOpen}>
                <a>+ Create a Character</a>
            </Button>
            <Dialog open={open} onClose={handleClose}>
                <DialogTitle>Roll a New Character</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        Enter your character's description and attributes below.
                    </DialogContentText>
                    {/* <FormControl> */}
                    <Box
                        component="form"
                        sx={{
                            '& .MuiTextField-root': { m: 1, width: '25ch' },
                        }}
                        noValidate
                        autoComplete="off"
                    >
                        <InputLabel id="race-dropdown-label">Race:</InputLabel>
                        <Select
                            labelId="race-dropdown-label"
                            id="race-dropdown"
                            label="Race"
                        >
                            <MenuItem value={'human'}>Human</MenuItem>
                            <MenuItem value={'orc'}>Orc</MenuItem>
                        </Select>
                        {/* </FormControl> */}
                        <br />
                        {/* <FormControl> */}
                        <InputLabel id="str-input-label">Strength: </InputLabel>
                        <Input type="integer" id="str-input" labelId="str-input-label" size="3" name="stats[strength]"></Input>
                        {/* <span class="stat-bonus" id="strength"></span>
                        <button type="button" class="die-img" disabled>20</button> */}
                        <Slider
                            aria-label="Strength"
                            defaultValue={0}
                            // getAriaValueText={valuetext}
                            valueLabelDisplay="auto"
                            min={1}
                            max={20}
                            sx={{ width: '10em', verticalAlign: 'middle' }}
                        />
                        {/* </FormControl> */}
                    </Box>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose}>Cancel</Button>
                    <Button onClick={handleClose}>Save & Close</Button>
                </DialogActions>
            </Dialog>
        </React.Fragment>
    );
}

export default ModalContent;
